import React, { useState, useEffect } from 'react';
import { db } from '../../firebaseConfig';
import { collection, getDocs } from 'firebase/firestore';
import EventList from '../Events/EventList';
import RegistrationForm from '../Events/RegistrationForm';
import ClubList from '../Clubs/ClubList'; // Import the ClubList component
import ClubRegistrationForm from '../Clubs/ClubRegistrationForm'; // Import the ClubRegistrationForm component

const StudentDashboard = () => {
  const [events, setEvents] = useState([]);
  const [clubs, setClubs] = useState([]); // State for clubs

  useEffect(() => {
    const fetchEvents = async () => {
      const querySnapshot = await getDocs(collection(db, 'events'));
      const eventsData = querySnapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
      }));
      setEvents(eventsData);
    };

    const fetchClubs = async () => {
      const querySnapshot = await getDocs(collection(db, 'clubs')); // Fetch clubs from Firestore
      const clubsData = querySnapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
      }));
      setClubs(clubsData);
    };

    fetchEvents();
    fetchClubs();
  }, []);

  return (
    <div>
      <h1>Student Dashboard</h1>
      <EventList events={events} />
      <ClubList clubs={clubs} /> {/* Display list of clubs */}
      <ClubRegistrationForm /> {/* Add Club Registration Form */}
      <RegistrationForm />
    </div>
  );
};

export default StudentDashboard;
